@Test
    public void runSampleBenchmark() throws Exception {
        // run the minimum possible number of iterations
        ChainedOptionsBuilder options = new OptionsBuilder()
                                            .mode(Mode.AverageTime)
                                            .forks(1)
                                            .result("jmh-report.json")
                                            .resultFormat(ResultFormatType.JSON)
                                            .operationsPerInvocation(1)
                                            .threads(1)
                                            .warmupForks(0)
                                            .warmupIterations(0)
                                            .measurementBatchSize(1)
                                            .measurementIterations(1)
                                            .timeUnit(TimeUnit.MICROSECONDS)
                                            .shouldFailOnError(true)
                                            // just run the SampleBenchmark, not other benchmarks
                                            .include(SampleBenchmark.class.getName() + ".*");
        new Runner(options.build()).run();
        assertTrue(Files.exists(Paths.get("jmh-report.json")));
    }

@Test
    @Issue("SECURITY-771")
    public void onlyAdminOrManageOrSystemReadCanReadAbout() throws Exception {
        final String ADMIN = "admin";
        final String USER = "user";
        final String MANAGER = "manager";
        final String READONLY = "readonly";
        final String MANAGER_READONLY = "manager-readonly";

        j.jenkins.setSecurityRealm(j.createDummySecurityRealm());
        j.jenkins.setAuthorizationStrategy(new MockAuthorizationStrategy()
                // full access
                .grant(Jenkins.ADMINISTER).everywhere().to(ADMIN)

                // Read access
                .grant(Jenkins.READ).everywhere().to(USER)

                // Read and Manage
                .grant(Jenkins.READ).everywhere().to(MANAGER)
                .grant(Jenkins.MANAGE).everywhere().to(MANAGER)

                // Read and System read
                .grant(Jenkins.READ).everywhere().to(READONLY)
                .grant(Jenkins.SYSTEM_READ).everywhere().to(READONLY)

                // Read, Manage and System read
                .grant(Jenkins.READ).everywhere().to(MANAGER_READONLY)
                .grant(Jenkins.MANAGE).everywhere().to(MANAGER_READONLY)
                .grant(Jenkins.SYSTEM_READ).everywhere().to(MANAGER_READONLY)
        );

        JenkinsRule.WebClient wc = j.createWebClient()
                .withThrowExceptionOnFailingStatusCode(false);

        { // user cannot see it
            wc.login(USER);
            HtmlPage page = wc.goTo("about/");
            assertEquals(HttpURLConnection.HTTP_FORBIDDEN, page.getWebResponse().getStatusCode());
        }

        { // admin can access it
            wc.login(ADMIN);
            HtmlPage page = wc.goTo("about/");
            assertEquals(HttpURLConnection.HTTP_OK, page.getWebResponse().getStatusCode());
            assertThat(page.getWebResponse().getContentAsString(), containsString("Mavenized dependencies"));
        }

        { // manager can access it
            wc.login(MANAGER);
            HtmlPage page = wc.goTo("about/");
            assertEquals(HttpURLConnection.HTTP_OK, page.getWebResponse().getStatusCode());
        }

        { // readonly can access it
            wc.login(READONLY);
            HtmlPage page = wc.goTo("about/");
            assertEquals(HttpURLConnection.HTTP_OK, page.getWebResponse().getStatusCode());
        }

        { // manager-readonly can access it
            wc.login(MANAGER_READONLY);
            HtmlPage page = wc.goTo("about/");
            assertEquals(HttpURLConnection.HTTP_OK, page.getWebResponse().getStatusCode());
        }
    }

@Test
    @Issue("SECURITY-1114")
    @For(AbstractItem.class)
    public void testAccess() throws Exception {
        j.jenkins.setSecurityRealm(j.createDummySecurityRealm());
        MockAuthorizationStrategy authorizationStrategy = new MockAuthorizationStrategy();
        authorizationStrategy.grant(Jenkins.READ).onRoot().toEveryone();
        authorizationStrategy.grant(Item.DISCOVER).everywhere().to("alice");
        authorizationStrategy.grant(Item.READ).everywhere().to("bob");
        j.jenkins.setAuthorizationStrategy(authorizationStrategy);

        j.createFreeStyleProject("myproject");

        // alice can discover project
        JenkinsRule.WebClient alice = j.createWebClient().login("alice");
        FailingHttpStatusCodeException e = assertThrows(FailingHttpStatusCodeException.class, () -> alice.goTo("bypass/myproject"));
        Assert.assertEquals("alice can discover", 403, e.getStatusCode());

        // bob can read project
        JenkinsRule.WebClient bob = j.createWebClient().login("bob");
        bob.goTo("bypass/myproject"); // success


        // carol has no permissions
        JenkinsRule.WebClient carol = j.createWebClient().login("carol");
        e = assertThrows(FailingHttpStatusCodeException.class, () -> carol.goTo("bypass/nonexisting"));
        Assert.assertEquals("carol gets 404 for nonexisting project", 404, e.getStatusCode());
        e = assertThrows(FailingHttpStatusCodeException.class, () -> carol.goTo("bypass/myproject"));
        Assert.assertEquals("carol gets 404 for invisible project", 404, e.getStatusCode());
    }

@Test
    public void test() throws Exception {
        final DateConverter dc = new DateConverter();
        ExecutorService es = Executors.newFixedThreadPool(10);

        List<Future> futures = new ArrayList<>();
        for (int i = 0; i < 10; i++) {
            futures.add(es.submit(() -> {
                for (int i1 = 0; i1 < 10000; i1++)
                    dc.fromString("2008-08-26 15:40:14.568 GMT-03:00");
                return null;
            }));
        }

        for (Future f : futures) {
            f.get();
        }
        es.shutdown();
    }

@PresetData(DataSet.NO_ANONYMOUS_READACCESS)
    @Email("http://markmail.org/message/on4wkjdaldwi2atx")
    @Test
    public void anonymousCanAlwaysLoadJARs() throws Exception {
        inboundAgents.createAgent(r, InboundAgentRule.Options.newBuilder().name("test").skipStart().build());
        JenkinsRule.WebClient wc = r.createWebClient();
        HtmlPage p = wc.withBasicApiToken(User.getById("alice", true)).goTo("computer/test/");

        // this fresh WebClient doesn't have a login cookie and represent JNLP launcher
        JenkinsRule.WebClient jnlpAgent = r.createWebClient();

        // parse the JNLP page into DOM to list up the jars.
        XmlPage jnlp = (XmlPage) wc.goTo("computer/test/jenkins-agent.jnlp", "application/x-java-jnlp-file");
        URL baseUrl = jnlp.getUrl();
        Document dom = new DOMReader().read(jnlp.getXmlDocument());
        for (Object jar : dom.selectNodes("//jar")) {
            URL url = new URL(baseUrl, ((Element) jar).attributeValue("href"));
            System.out.println(url);

            // now make sure that these URLs are unprotected
            Page jarResource = jnlpAgent.getPage(url);
            assertTrue(jarResource.getWebResponse().getContentType().toLowerCase(Locale.ENGLISH).startsWith("application/"));
        }
    }

