targets.query:
from(bucket: "${bucket}")
  |> range(start: ${__from:date}, stop: ${__to:date})
  |> filter(fn: (r) => r._measurement == "cpu" and r._field == "usage_idle" and r.cpu == "cpu-total" and r.host =~ /$server$/)
  |> aggregateWindow(every: ${inter}, fn: mean, createEmpty: false)
  |> last()
  |> map(fn: (r) => ({
      _time: r._time,
      usage: 100.0 - r._value
  }))

import "strings"

from(bucket: "${bucket}")
  |> range(start: ${__from:date}, stop: ${__to:date})
  |> filter(fn: (r) => r._measurement == "docker_log" and r._field == "message" and r.container_name == "${container}")
  |> keep(columns: ["_time", "_value"])
  |> sort(columns: ["_time"])
  |> reduce(fn: (r, accumulator) => ({
    _time: r._time,
    _value: strings.joinStr(arr: [accumulator._value, r._value], v: "\n")// 
  }), identity: {
    _time: time(v: 0),
    _value: ""
  })

from(bucket: "${bucket}")
  |> range(start: ${__from:date}, stop: ${__to:date})
  |> filter(fn: (r) => r._measurement == "cpu" and r.host =~ /$server$/ and r.cpu == "cpu-total")
  |> aggregateWindow(every: $inter, fn: mean, createEmpty: false)
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({
      r with
      total: r.usage_user + r.usage_system + r.usage_softirq + r.usage_steal + r.usage_nice + r.usage_irq + r.usage_iowait + r.usage_guest + r.usage_guest_nice
  }))
  |> keep(columns: ["_time", "usage_user", "usage_system", "usage_softirq", "usage_steal", "usage_nice", "usage_irq", "usage_iowait", "usage_guest", "usage_guest_nice", "total"])
